// Populate the sidebar
//
// This is a script, and not included directly in the page, to control the total size of the book.
// The TOC contains an entry for each page, so if each page includes a copy of the TOC,
// the total size of the page becomes O(n**2).
class MDBookSidebarScrollbox extends HTMLElement {
    constructor() {
        super();
    }
    connectedCallback() {
        this.innerHTML = '<ol class="chapter"><li class="chapter-item expanded affix "><a href="introduction.html">Introduction</a></li><li class="chapter-item expanded affix "><li class="part-title">Rust (zarrs)</li><li class="chapter-item expanded "><a href="installation.html"><strong aria-hidden="true">1.</strong> Installation</a></li><li class="chapter-item expanded "><a href="stores.html"><strong aria-hidden="true">2.</strong> Stores</a></li><li class="chapter-item expanded "><a href="groups.html"><strong aria-hidden="true">3.</strong> Groups</a></li><li class="chapter-item expanded "><a href="arrays.html"><strong aria-hidden="true">4.</strong> Arrays</a></li><li><ol class="section"><li class="chapter-item expanded "><a href="arrays/array_init.html"><strong aria-hidden="true">4.1.</strong> Initialisation</a></li><li class="chapter-item expanded "><a href="arrays/array_read.html"><strong aria-hidden="true">4.2.</strong> Reading</a></li><li class="chapter-item expanded "><a href="arrays/array_write.html"><strong aria-hidden="true">4.3.</strong> Writing</a></li></ol></li><li class="chapter-item expanded "><a href="v2_to_v3.html"><strong aria-hidden="true">5.</strong> Converting Zarr V2 to V3</a></li><li class="chapter-item expanded "><a href="extensions.html"><strong aria-hidden="true">6.</strong> Extension Points</a></li><li><ol class="section"><li class="chapter-item expanded "><a href="extensions/codec.html"><strong aria-hidden="true">6.1.</strong> Codecs</a></li></ol></li><li class="chapter-item expanded "><li class="part-title">Bindings</li><li class="chapter-item expanded "><a href="zarrs_python.html"><strong aria-hidden="true">7.</strong> Python (zarrs_python)</a></li><li class="chapter-item expanded "><a href="zarrs_ffi.html"><strong aria-hidden="true">8.</strong> C/C++ (zarrs_ffi)</a></li><li class="chapter-item expanded affix "><li class="part-title">CLI Tools</li><li class="chapter-item expanded "><a href="zarrs_tools.html"><strong aria-hidden="true">9.</strong> zarrs_tools</a></li><li><ol class="section"><li class="chapter-item expanded "><a href="zarrs_tools/docs/zarrs_reencode.html"><strong aria-hidden="true">9.1.</strong> zarrs_reencode: reencode/rechunk Zarr arrays</a></li><li class="chapter-item expanded "><a href="zarrs_tools/docs/zarrs_ome.html"><strong aria-hidden="true">9.2.</strong> zarrs_ome: convert to OME-Zarr multiscales</a></li><li class="chapter-item expanded "><a href="zarrs_tools/docs/zarrs_filter.html"><strong aria-hidden="true">9.3.</strong> zarrs_filter: apply image transformations</a></li><li class="chapter-item expanded "><a href="zarrs_tools/docs/zarrs_validate.html"><strong aria-hidden="true">9.4.</strong> zarrs_validate: validate array equivalence</a></li><li class="chapter-item expanded "><a href="zarrs_tools/docs/zarrs_info.html"><strong aria-hidden="true">9.5.</strong> zarrs_info: get array/group information</a></li><li class="chapter-item expanded "><a href="zarrs_tools/docs/zarrs_binary2zarr.html"><strong aria-hidden="true">9.6.</strong> zarrs_binary2zarr: convert piped binary data to Zarr</a></li></ol></li></ol>';
        // Set the current, active page, and reveal it if it's hidden
        let current_page = document.location.href.toString().split("#")[0].split("?")[0];
        if (current_page.endsWith("/")) {
            current_page += "index.html";
        }
        var links = Array.prototype.slice.call(this.querySelectorAll("a"));
        var l = links.length;
        for (var i = 0; i < l; ++i) {
            var link = links[i];
            var href = link.getAttribute("href");
            if (href && !href.startsWith("#") && !/^(?:[a-z+]+:)?\/\//.test(href)) {
                link.href = path_to_root + href;
            }
            // The "index" page is supposed to alias the first chapter in the book.
            if (link.href === current_page || (i === 0 && path_to_root === "" && current_page.endsWith("/index.html"))) {
                link.classList.add("active");
                var parent = link.parentElement;
                if (parent && parent.classList.contains("chapter-item")) {
                    parent.classList.add("expanded");
                }
                while (parent) {
                    if (parent.tagName === "LI" && parent.previousElementSibling) {
                        if (parent.previousElementSibling.classList.contains("chapter-item")) {
                            parent.previousElementSibling.classList.add("expanded");
                        }
                    }
                    parent = parent.parentElement;
                }
            }
        }
        // Track and set sidebar scroll position
        this.addEventListener('click', function(e) {
            if (e.target.tagName === 'A') {
                sessionStorage.setItem('sidebar-scroll', this.scrollTop);
            }
        }, { passive: true });
        var sidebarScrollTop = sessionStorage.getItem('sidebar-scroll');
        sessionStorage.removeItem('sidebar-scroll');
        if (sidebarScrollTop) {
            // preserve sidebar scroll position when navigating via links within sidebar
            this.scrollTop = sidebarScrollTop;
        } else {
            // scroll sidebar to current active section when navigating via "next/previous chapter" buttons
            var activeSection = document.querySelector('#sidebar .active');
            if (activeSection) {
                activeSection.scrollIntoView({ block: 'center' });
            }
        }
        // Toggle buttons
        var sidebarAnchorToggles = document.querySelectorAll('#sidebar a.toggle');
        function toggleSection(ev) {
            ev.currentTarget.parentElement.classList.toggle('expanded');
        }
        Array.from(sidebarAnchorToggles).forEach(function (el) {
            el.addEventListener('click', toggleSection);
        });
    }
}
window.customElements.define("mdbook-sidebar-scrollbox", MDBookSidebarScrollbox);
